import React from 'react';
import Cell from './Cell';
import styled from 'styled-components';
import { connect } from 'react-redux';

const BoardContainer = styled.div`
  display: flex;
  flex-direction: row;
  width: 500px;
  height: 500px;
  background-color: gray;
  margin: 0 auto;
  flex-wrap: wrap;
`;

/*
//I don't think I need a class for board, fix this later
class Board extends Component {

  constructor(props) {
    super(props);
    this.state = {
      board: props.board,
      delay: props.delay,
    };
  }
  */

function Board ({board, isRunning, onCellClick}) {
  console.log(onCellClick)
  return (
    <BoardContainer>
      {board.map((cell, index) => (
        <Cell isAlive={cell} id={index} isRunning={isRunning} onClick={onCellClick} />
      ))}
    </BoardContainer>
  );

}

//I don't think mapStateToProps is necessary here since on <Cell> and <Controls> modify board
const mapStateToProps = state => ({
  board: state.board
});

export default connect(
  mapStateToProps
)(Board);
