import React, { Component } from 'react';
import './App.css';
import Board from './components/Board';
import Controls from './components/Controls';
//import { iterateBoard } from './actions/';
import { initBoard, iterateGeneration , clearBoard } from './lifeFunctions/';

class App extends Component {
  constructor(props) {
    super(props);
    this.onCellClick = this.onCellClick.bind(this);
    this.onIterate = this.onIterate.bind(this);
    this.state = {
      //board: initBoard(),
      isRunning: false,
      board: initBoard(),
      delay: 2, //ms
      generation: 0,
      iterating: false,
      error: null
    };
    this.currentBoard = this.state.board.concat([]);
  }

  //only need to store values on state that are needed to trigger component re-renders
  //removed board from state since onClick cell and button clicks (iterate, etc.) are
  //all thats needed for re-render (not board itself)
  //-can probably use generation for loop increment re-render if i hit issues
  //once working, i should refactor to remove anything else from state not used for re-render
  onCellClick(id) {
    console.log('cell click')
    this.currentBoard[id] = !this.currentBoard[id]
  };

  clearBoard() {
    this.currentBoard = clearBoard
    this.setState(prevState => {
      return {
        ...prevState,
        generation: 0,
        board: this.currentBoard.concat([])
      };
    });
  }

  onIterate(){
    console.log('*iterating*')
    let newBoard = iterateGeneration(this.currentBoard)
    console.log('does newBoard == oldBoard? ' + (newBoard === this.state.board))
    this.currentBoard = newBoard
    this.setState(prevState => {
      return {
        ...prevState,
        generation: prevState.generation + 1,
        board: newBoard.concat([])
      };
    });
  }

  //need code here to surpress entire board refresh and manage new cell appearance with toggle state 
  //<Controls board={this.props.board} generation={generation} isRunning={isRunning} delay={delay} onIterateClick={this.onIterateClick} />

  render() {
    let {isRunning, generation, delay} = this.state
    return (
      <div className="App">
        <Board board={this.state.board} isRunning={isRunning} onClick={this.onCellClick} generation={generation} />
        <Controls board={this.state.board} generation={generation} isRunning={isRunning} delay={delay} onIterateClick={this.onIterate} onClearClick={this.clearBoard} />
      </div>
    );
  }
}

export default App;
