import {
  START_BOARD_INIT,
  BOARD_INIT_SUCCESS,
} from '../actions';

//import initBoard from ../lifeFunctions/lifeFunctions.js

function initBoard(){
  let board = []
  let randomSeed = 0
  //this config makes every 1 in 10 random squares in 100x100
  for (let c = 0; c < 70; c++){
    randomSeed = Math.floor(Math.random() * 11);
    if (randomSeed === 0) {
      board[c] = true
    }else{
      board[c] = false
    }
  }
  return board
}

const initState = {
  board: initBoard(),
  isRunning: false,
  delay: 2, //ms
  error: null,
}

const reducer = (state = initState, action) => {
  switch (action.type){
    //change view
    case START_BOARD_INIT:
      return {
        ...state,
        error: null
      };
    case BOARD_INIT_SUCCESS:
      return {
        ...state,
        error: null
      };

    default: 
      return state;
  }
};

export default reducer;
