import React, { Component } from 'react';
import styled from 'styled-components';

const LifeCell = styled.div`
  border: .5px solid black;
  width: 9px;
  height: 9px;
  border-radius: 25%;
  background-color : ${state => {
    //I don't fully understand how passing values into this works
    console.log('color: ' + state.isAlive)
    if(state.isAlive === true){
      return 'green';
    } else {
      return 'black';
    }
  }};
`;
//pure component (import pure component)
class Cell extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isAlive: props.isAlive,
      isAliveProp: props.isAlive
    };
  }
  /*
  //this is called before rendering.
  static getDerivedStateFromProps(nextProps, prevState) {
    if (nextProps.isAlive !== prevState.isAliveProp){
      return {
        isAlive: nextProps.isAlive,
        isAliveProp: nextProps.isAlive
      }
    }
    return null
  }
  */
  handler = () => {
    if(this.props.isRunning === false){
      this.setState(prevState => {
        return {
          ...prevState,
          isAlive: !prevState.isAlive
        }
      });
      this.props.isAlive = this.state.isAlive
      this.props.onClick(this.props.id)
    }
  };

  render() {
    console.log(this.props.id)
    console.log('state.isAlve: ' + this.state.isAlive)
    console.log('props.isAlve: ' + this.props.isAlive)
    return(
      <LifeCell id={this.props.id} isAlive={this.props.isAlive} onClick={this.handler}  />
    )
  }
};

export default Cell;
