import React, { Component } from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';
import { toggleCellUpdate } from '../actions/';

const LifeCell = styled.div`
  border: .5px solid black;
  width: 9px;
  height: 9px;
  background-color : ${props => {
    //I don't fully understand how passing values into this works
    if(props.isAlive === true){
      return 'green';
    } else {
      return 'black';
    }
  }};
`;

class Cell extends Component {

  constructor(props) {
    super(props);
    this.state = {
      //currentState: props.isAlive,
      //id: props.id,
      //isRunning: props.isRunning
    };
  }

  /*
    handleClick = event => {
      if(this.props.isRunning === false){
        this.props.onCellClick(this.props.id)
      }
    };
  */

  handler(id, onCellClick) {
    console.log('*')
    return () => onCellClick(id);
  };

  render() {
    //console.log('**')
    //console.log(this.props);
    return(
      /*<LifeCell id={this.props.id} isAlive={this.props.isAlive} onClick={event => this.handleClick(event)} />*/
      <LifeCell id={this.props.id} isAlive={this.props.isAlive} onClick={event => this.handler(this.props.id, this.props.onCellClick)} />
    )
  }
}

export default connect(
  //{ toggleCellUpdate }
)(Cell);
