import React, { Component } from 'react';
import Cell from './Cell';
import styled from 'styled-components';

const BoardContainer = styled.div`
  display: flex;
  flex-direction: row;
  width: 500px;
  height: 500px;
  background-color: gray;
  margin: 0 auto;
  flex-wrap: wrap;
`;

//I don't think I need a class for board, fix this later
class Board extends Component {

  constructor(props) {
    super(props);
    this.state = {
      board: props.board,
      delay: props.delay,
    };
  }

  handleClick = (event, index) => {
    console.log("!")
    if(this.props.isRunning === false){
      //console.log(this.state.currentState)
      console.log(event)
      let board = this.state.board
      board[this.state.id] = !board[this.state.id]

      this.props.toggleCellUpdate(board);
    }
  }

  render() {
    return (
      <BoardContainer>
        {this.props.board.map((cell, index) => (
          <Cell onClick={event => this.handleClick(event)} isAlive={cell} id={index} />
        ))}
      </BoardContainer>
    );
  }
}

export default Board;
