import React, { Component } from 'react';
import './App.css';
import Board from './components/Board';
import Controls from './components/Controls';
import { connect } from 'react-redux';
import { iterateBoard, toggleCellUpdate } from './actions/';


class App extends Component {
  constructor(props) {
    super(props);
    this.onCellClick = this.onCellClick.bind(this);
  } 

  componentDidMount() {
  }

  componentDidUpdate(prevProps, prevState){
  }

  onCellClick(id) {
    console.log('**');
    let board = this.state.board
    console.log(this.props.board[id]);
    board[id] = !board[id]
    this.setState(prevState => {
      return {
        ...prevState,
        board: board
      };
    });
    console.log(this.state.board[id]);
    //need code here to surpress entire board refresh and manage new cell appearance with toggle state 
  };
  render() {
    return (
      <div className="App">
        <Board board={this.props.board} isRunning={this.props.isRunning} onClick={this.onCellClick}/>
        <Controls {...this.props} />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  board: state.board,
  delay: state.delay,
  isRunning: state.isRunning,
  generation: 0,
  iterating: false,
  error: state.error
});

export default connect(
  mapStateToProps,
  { iterateBoard, toggleCellUpdate }
)(App);
