import React, { Component } from 'react';
import './App.css';
import Board from './components/Board';
import Controls from './components/Controls';
import { connect } from 'react-redux';
import { iterateBoard, toggleCellUpdate } from './actions/';


class App extends Component {
  constructor(props) {
    super(props);
    this.onCellClick = this.onCellClick.bind(this);
    this.state = {
      board: props.board
    };
  } 

  componentDidMount() {
  }

  componentDidUpdate(prevProps, prevState){
  }

  onCellClick(id) {
    let board = this.props.board
    board[id] = !board[id]
    this.setState(prevState => {
      return {
        ...prevState,
        board: board //board.concat([])
      };
    });

    //need code here to surpress entire board refresh and manage new cell appearance with toggle state 
  };
  render() {
    return (
      <div className="App">
        <Board board={this.state.board} isRunning={this.props.isRunning} onClick={this.onCellClick} />
        <Controls board={this.props.board} />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  board: state.board,
  delay: state.delay,
  isRunning: state.isRunning,
  generation: 0,
  iterating: false,
  error: state.error
});

export default connect(
  mapStateToProps,
  { iterateBoard, toggleCellUpdate }
)(App);
