import React, { Component } from 'react';
import './App.css';
import Board from './components/Board';
import Controls from './components/Controls';
import { connect } from 'react-redux';
import { iterateBoard, toggleCellUpdate } from './actions/';


class App extends Component {
  constructor(props) {
    super(props);
    this.onCellClick = this.onCellClick.bind(this);
    //this.state = {
    //};
  } 

  componentDidMount() {
  }

  componentDidUpdate(prevProps, prevState){
  }

  onCellClick(id) {
    console.log('**');
    let board = this.props.board
    board[id] = !board[id]
    this.state.board = board
    //need code here to surpress entire board refresh and manage new cell appearance with toggle state 
  };

  render() {
    if(this.props.iterating){
      console.log('-------------- LOADING --------------');
      return <h4>Loading...</h4>
    }else{
      return (
        <div className="App">
          <Board {...this.props} onClick={this.onCellClick}/>
          <Controls {...this.props} />
        </div>
      );
    }
  }
}

const mapStateToProps = state => ({
  board: state.board,
  delay: state.delay,
  isRunning: state.isRunning,
  generation: 0,
  iterating: false,
  error: state.error
});

export default connect(
  mapStateToProps,
  { iterateBoard, toggleCellUpdate }
)(App);
