import React, { Component } from 'react';
import './App.css';
import Board from './components/Board';
import Controls from './components/Controls';
import { connect } from 'react-redux';
import { iterateBoard, toggleCellUpdate } from './actions/';


class App extends Component {
  constructor(props) {
    super(props);
    this.onCellClick = this.onCellClick.bind(this);
    this.state = {
      board: props.board
    };
  } 

  componentDidMount() {
  }

  componentDidUpdate(prevProps, prevState){
  }

  onCellClick(id) {
    let board = this.props.board
    board[id] = !board[id]
    this.setState(prevState => {
      return {
        ...prevState,
        board: board //board.concat([])
      };
    });
  }
/*
  onIterateClick(board, generation){
    this.setState(prevState => {
      return {
        ...prevState,
        board: board.concat([]),
        generation: generation
      };
    });
  }
*/
  //need code here to surpress entire board refresh and manage new cell appearance with toggle state 
  render() {
    let {board, isRunning, generation, delay} = this.props
    return (
      <div className="App">
        <Board board={board} isRunning={isRunning} onClick={this.onCellClick} />
        <Controls board={board} generation={generation} isRunning={isRunning} delay={delay} onIterateClick={this.onIterateClick} />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  board: state.board,
  delay: state.delay,
  isRunning: state.isRunning,
  generation: 0,
  iterating: false,
  error: state.error
});

export default connect(
  mapStateToProps,
  { iterateBoard, toggleCellUpdate }
)(App);
